function loadScript(url, callback){
    var script = document.createElement("script");
    script.type = "text/javascript";
    script.src = url;
    script.onload = callback;
    document.head.appendChild(script);
}

// URL of the jQuery library
var jqueryUrl = "https://stackpath.bootstrapcdn.com/bootstrap/4.5.0/js/bootstrap.min.js";


// Load jQuery and execute your code when it's done loading
loadScript(jqueryUrl, function(){
    // Now jQuery is available, and you can start using it
    // Your code that depends on jQuery goes here
});